import React from 'react';
import './Campo.css';

interface CampoDeTextoProps {
  label: string;
  valor: string;
  aoAlterado: (novoValor: string) => void;
  obrigatorio: boolean;
  type?: string; // Tornando 'type' opcional
}

const Campo: React.FC<CampoDeTextoProps> = ({ label, valor, aoAlterado, obrigatorio, type = "text" }) => {
  const aoDigitado = (evento: React.ChangeEvent<HTMLInputElement>) => {
    aoAlterado(evento.target.value);
  };

  return (
    <div className={`campo campo-${type}`}>
      <label className="campolabel">{label}</label>
      <input
        type={type}
        value={valor}
        onChange={aoDigitado}
        required={obrigatorio}
        placeholder={`Digite o seu ${label}`}
      />
    </div>
  );
};

export default Campo;
